import React from 'react';

type FileType = {
    id: string;
    name: string;
    isDir: boolean;
    path?: string;
    parentId?: string;
    lastModified?: number;
};
type FileSystemType = FileType[];

declare enum ViewStyle {
    List = "list",
    Icons = "icons"
}

interface IFileManagerProps {
    fs: FileSystemType;
    viewOnly?: boolean;
    onDoubleClick?: (id: string) => Promise<void>;
    onRefresh?: (id: string) => Promise<void>;
    onUpload?: (fileData: any, folderId: string) => Promise<void>;
    onCreateFolder?: (folderName: string) => Promise<void>;
    onDelete?: (fileId: string) => Promise<void>;
}
declare const ReactFileManager: ({ fs, viewOnly, onDoubleClick, onRefresh, onUpload, onCreateFolder, onDelete, }: IFileManagerProps) => React.JSX.Element;

export { FileSystemType, FileType, IFileManagerProps, ReactFileManager, ViewStyle };
